    }
  }

  goHome = event => {
    this.setState({isMovie: false, movieClick: {}, isHome: true})
  }

  selectMovie = event => {
    this.setState({ currentMovie: event.target.id });
  }

  render() {
    return (
      <>
        <Header />
        <div className="App">
          // {!this.state.isHome && <Film />}

          /* <Movies movies={this.state.movies} />

