import React, { Component } from 'react';
import Header from '../Header/Header';
import Movies from '../Movies/Movies';
import Film from '../Film/Film';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      movies: [],
      currentMovie: '',
      isHome: true,
      isLoading: true,
      error: false
    }
  }

  componentDidMount() {
    fetch('https://rancid-tomatillos.herokuapp.com/api/v2/movies/')
      .then(response => response.json())
      .then(films => this.checkForError(films))
      .catch(error => this.setState({isLoading:false, error: true}))
  }

  checkForError = films => {
    if(films.error) {
      this.setState({error: true, isLoading: false})
    } else {
      this.setState({movies: films.movies, isLoading: false})
    }
  }

  goHome = () => {
    this.setState({ currentMovie: '', isHome: true})
  }

  selectMovie = (event) => {
    this.setState({ currentMovie: event.target.closest('article').id, isHome: false });
  }

  render() {
    return (
      <>
        <Header goHome={this.goHome}/>
        <div className="App">
          {this.state.isLoading && <><img src='https://media.giphy.com/media/131tNuGktpXGhy/giphy.gif' alt='loading' /><h2>Please wait...</h2></>}
          {this.state.error && <h2>ðŸ’¥We are having a technical difficulty.ðŸ’¥</h2>}
          {!this.state.isHome
            ? <Film currentMovie={this.state.currentMovie} />
            : <Movies
                movies={this.state.movies}
                selectMovie={this.selectMovie}
              />
          }
        </div>
      </>
    );
  }
}

export default App;
